	<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>		<header class="entry-header">			<?php if ( is_single() ) : ?>			<h1 class="entry-title"><?php the_title(); ?></h1>			<?php else : ?>			<h1 class="entry-title">				<?php if( is_sticky() ) echo '<span>置顶</span>' ?><?php 					$t1=$post->post_date;					$t2=date("Y-m-d H:i:s");					$diff=(strtotime($t2)-strtotime($t1))/3600;					if($diff<24){echo "<span>New</span>";} 					else{echo "";} 				?><a href="<?php the_permalink(); ?>" rel="bookmark"><?php the_title(); ?></a>			</h1>			<?php endif; ?>			<div class="meta-top1">				<div class="meta-top2">			</div>				<?php if ( comments_open() ) : ?>				<div class="comments-link">					<i class="fa fa-comments"></i><?php comments_popup_link('<span class="leave-reply">'.__('暂无评论','hmjblog').'</span>',__('1条评论','hmjblog' ), __( '%条评论', 'hmjblog')); ?>　<i class="fa fa-eye"></i><?php post_views('', '次浏览'); ?>				</div>				<?php endif; ?>				<?php if ( !comments_open()) : ?>				<div class="comments-link" style="margin: 10px 0px;">					<i class="fa fa-comments"></i><span class="leave-reply">评论关闭</span>　<i class="fa fa-eye"></i><?php post_views('', '次浏览'); ?>				</div>				<?php endif; ?>			</div>		</header>						<div class="post-thumbnail">			<a href="<?php echo get_permalink(); ?>" title="<?php the_title(); ?>"><?php if (  ! post_password_required() && ! is_attachment() && !is_single() ) :				the_post_thumbnail();			endif; ?></a>			</div>		<?php if ( !is_single() ) : ?>		<div class="entry-summary">			<?php the_excerpt() ?> 		</div>		<?php else : ?>		<div class="entry-content">			<?php the_content( __( '阅读全文 <span class="meta-nav">&rarr;</span>', 'hmjblog' ) ); ?>			<?php wp_link_pages( array( 'before' => '<div class="page-links">' . __( 'Pages:', 'hmjblog' ), 'after' => '</div>' ) ); ?>		</div>		<?php endif; ?>	</article>